import React, { useEffect, useState } from 'react'

function Card(props) {

  //get Variables from Parent
  const { name, area, altSpellings, flags, population, region, capital, ccn3 } = props.country;

  //favCoubtry
  let [favCountry, setFavCountry] = useState({

    area: 0,
    img: "",
    name: "country",
  });
  
  /** Favorite Section **/
  //Start of Dragging
    function dragCard(area, img, name){

        img = img.toLowerCase();
        let imagURL = `https://flagcdn.com/${img}.svg`;
        favCountry.area = area;
        favCountry.img = imagURL;
        favCountry.name = name;
        props.handleFavCountry(favCountry); // Pass the favCountry data to the parent component
        console.log("start");
    }
//End of Dragging
    function endDragCard(){

        favCountry = {

            area: 0,
            img: "",
            name: "country",
        };
        console.log("end");
    }

    let favBtnContent = ``;
    // faviorite countries from local storage
    let [favCountries, setFavCountries] = useState([]);
    let jsonlocCountries = localStorage.getItem("favCountries");
    if (jsonlocCountries) {

        favCountries = JSON.parse(jsonlocCountries);
    }



    
    return (
      <>
       <div className="col-lg-4 col-md-6 col-sm-6 col-xsm-12 mb-3">
            <div draggable="true" onDragStart={()=>dragCard(area, altSpellings[0], name.official)} onDragEnd={()=>endDragCard()} className="card border-0 position-relative m-1 m-xl-3 mt-0 ${darkModeClass}">
                <a className="position-relative link" href="pages/flagdet.html?code=${ccn3}">
                <div className="image">
                    <img loading='lazy' alt="home-img" src={`${flags.svg}`} />
                </div>
                </a>
                <div className="card-body flag-card form ${darkModeClass}">
                <h5 className="card-title px-2 my-1 mid-bold text-truncate home-size">{name.official}</h5>
                <ul className="px-2 mt-3 mb-5">
                    <li className="home-size"><span className="mid-bold home-size">Population:</span> {population}</li>
                    <li className="home-size"><span className="mid-bold home-size">Region:</span> {region}</li>
                    <li className="home-size"><span className="mid-bold home-size">Capital:</span> {capital}</li>
                </ul>
                </div>
                <div className="fav-click d-md-none fav-star bg-transparent position-absolute bottom-0 end-0" style={{fontFamily: 'FontAwesome'}}>{favBtnContent}</div>
            </div>
        </div>

      </>
    );
  }
  
  export default Card;
