import { useState, useEffect } from 'react';

const DarkMode = () => {
  const [darkMode, setDarkMode] = useState(localStorage.getItem('darkMode') === 'true');
  const toggleDarkMode = () => {
    const newDarkMode = !darkMode;
    localStorage.setItem('darkMode', newDarkMode);
    setDarkMode(newDarkMode);
  };

  useEffect(() => {
    const elements = document.getElementsByClassName('light');
    const modeIcone = document.getElementById('mode-icone');

    const toDark = () => {
      document.body.classList.toggle('dark-theme');
      modeIcone.classList.toggle('fa-moon-o');
      modeIcone.classList.toggle('fa-sun-o');
      for (const element of elements) {
        element.classList.toggle('spic-dark-mode');
      }
    };

  }, [darkMode]);

  return { darkMode, toggleDarkMode };
};

export default DarkMode;
