import React, { useEffect, useState } from "react";
import { useDarkMode } from "../../customeMethods/DarkModeContext";

function FavioriteSection({ favCountries }, { dispFaviorites }) {
  const { darkMode } = useDarkMode();
  let favoritBox = document.getElementById("favourit-countries");

  //Hover on Box
  function favDragOver(e) {
    e.preventDefault();
    favoritBox.classList.add("hovered");
  }

  //Leave the Box
  function favDragLeave() {
    favoritBox.classList.remove("hovered");
  }

  //Dropping the Item
  async function favDroped(e) {
    e.preventDefault();
    favoritBox.classList.remove("hovered");
    await storeInLocalStorage(favCountry);
    setFavCountry({
      area: 0,
      img: "",
      name: "country",
    });
  }
  // disply visibillity countries
  function dispFaviorites() {
    return favCountries.map((countrey) => (
      <div className="d-flex justify-content-md-between">
        <div className="d-flex gap-2 align-items-center w-75">
          <div className="fav-image">
            <img alt="fav-img" src={`${countrey.img}`} />
          </div>
          <h5 className="card-title p-0 m-0 mid-bold text-truncate detailes-size">
            {countrey.name}
          </h5>
        </div>
        <button
          onClick={() => deleteCountry(countrey.area)}
          className={`bg-transparent py-0 px-2 mid-bold detailes-size ${
            darkMode ? " spic-dark-mode" : "light"
          }`}
        >
          x
        </button>
      </div>
    ));
  }
  useEffect(() => {
    dispFaviorites();
  }, []);
  useEffect(() => {
    dispFaviorites();
  }, [favCountries]);
  return (
    <aside
      className={`fav ${
        darkMode ? " spic-dark-mode" : "light"
      } f-flex flex-column gap-2 d-none d-md-block p-1 d-none d-md-block bg-white`}
    >
      <div className="px-3 py-4">
        <h4 className="h5 mid-bold home-size">Favourites</h4>
      </div>
      <div
        onDragOver={favDragOver}
        onDragLeave={favDragLeave}
        onDrop={favDroped}
        className="px-0 favourit-content d-flex flex-column gap-2"
        id="favourit-countries"
      >
        {favCountries ? dispFaviorites() : ""}
      </div>
    </aside>
  );
}

export default FavioriteSection;
