import React, { useEffect, useState } from 'react'
import {useDarkMode} from '../customeHooks/DarkModeContext'

function RegionFilter(props) {

    const {darkMode} = useDarkMode(); //dark mode
    function handaleChange(event){
    let regionValue = event.target.value;
    props.handaleFilterOption(regionValue);
    }

    useEffect(() => {
    }, []);

  // Return Elements:
  return (

    <select className={`form-select ${darkMode ? "spic-dark-mode" : "light"}`} aria-label="Default select example" onChange={handaleChange} name="region" id="region">
        <option value="noValue" defaultValue>Filter by</option>
        <option value="noValue">No Filter</option>
        <option value="fav">Favourites</option>
        <option value="Africa">Africa</option>
        <option value="Americas">America</option>
        <option value="Asia">Asia</option>
        <option value="Europe">Euorpe</option>
        <option value="Oceania">Oceania</option>
    </select>
  );
}

export default RegionFilter;
