import React, { useEffect, useState } from 'react'
import DarkMode from '../customeHooks/DarkMode';

function SearchBar() {
    const { darkMode, toggleDarkMode } = DarkMode();
    return (
    <>
    <div className="search w-50 gap-1 d-flex flex-column flex-md-row flex-wrap justify-content-start justify-content-md-between g-4 g-md-0 align-items-md-center my-4">
        <div className={`input-group ${darkMode ? " spic-dark-mode" : "light"} bg-transparent`}>
            <div className={`custome-search bg-white d-flex justify-content-start ${darkMode ? " spic-dark-mode" : "light"}`}>
                <span className="spic-dark-mode border-0 input-group-text bg-white p-4 gray-color" style={{fontFamily: 'FontAwesome'}}><i className="fas fa-search" /></span>
                <div className="form-floating gray-color" id="search-bar">
                    <input className="spic-dark-mode form-control w-100 border-0" id="floatingInputGroup1" type="search" placeholder="Search for a country..." aria-label="Search" />
                </div>
            </div>           
        </div>
    </div>
    </>
  );
}

export default SearchBar;
