import React, { useEffect, useState } from 'react'
import axios from 'axios';

import SearchBar from '../componunts/SearchBar';
import RegionFilter from '../componunts/RegionFilter';

function Home() {
 
  const [countries, setCountries] = useState([]);
  const [visipCountries, setVisipCountries] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterOption, setFilterOption] = useState('');

  let errorMessage = ``;
// function to get all originalCountries:
async function getAllCountries() {
  try {
    const response = await fetch("https://restcountries.com/v3.1/all");
    const data = await response.json();
    setCountries(data);
    setVisipCountries(data);
    console.log(data); // Log the fetched data here
  } catch (error) {
    errorMessage = `
      <div class="w-100 h-100 d-flex flex-column align-items-center">
        <div class="alert alert-danger my-4 mid-bold w-100 d-flex justify-content-center">Error!!!</div>
        <div class="my-4 mid-bold">${error.message}, Please wait for us to solve the problem.</div>
      </div>
    `;

    document.body.innerHTML = errorMessage;
    console.error("Error in fetching originalCountries:", error);
  }
}

useEffect(() => {
     getAllCountries();
}, []);



  return (
    <>
      <div className='searchSection my-5 d-flex justify-content-between align-items-center'>
          <SearchBar />
          <RegionFilter />
      </div>
      <div className="content d-flex gap-4">
          <aside className="fav light f-flex flex-column gap-2 d-none d-md-block p-1 d-none d-md-block bg-white">
            <div className="px-3 py-4">
              <h4 className="h5 mid-bold home-size">Favourites</h4>
            </div>
            <div onDragOver="favDragOver(event)" onDragLeave="favDragLeave(event)" onDrop="favDroped(event)" className="px-0 favourit-content d-flex flex-column gap-2" id="favourit-countries">
            </div>
          </aside>
          <div className="detailes-contain">
            <div className="row" id="flag-row">
            </div>
          </div>
      </div>

  </>
  );
}

export default Home;
