import React, { useEffect, useState } from 'react'
import { useSearchParams } from 'react-router-dom'
import axios from 'axios';
import { useNavigate } from 'react-router-dom';


//custom hooks
import {useDarkMode} from '../customeHooks/DarkModeContext'


function FlagDetails() {
  //Dark or not
  const {darkMode} = useDarkMode();

  //get code from url
  let [searchParams, setSearchParams] = useSearchParams();
  let code = searchParams.get('countryID');

  //store country
  let [countrey, setCountrey] = useState();

  //function fetch api country
  async function getCountry(){

    try{

      let {data} = await axios.get(`https://restcountries.com/v3.1/alpha/${code}`);
      setCountrey(data);
    }catch(error) {
      
      console.error('Fetch operation failed: ', error);
    }
   
  }

  let borderNo;
  let nativeName = "";
  let currency = "";
  let lang = "";



  if(countrey !== undefined){
    // get Borders
    borderNo = countrey[0].borders;
    
    //get NativeName
    for(const coName in countrey[0].name.nativeName){

        nativeName = countrey[0].name.nativeName[coName].common;
    }
    //get currencies
    let noCurr = 0;
    for(const type in countrey[0].currencies){

        if(noCurr >= 1){

            currency += ", ";
        }
        currency += type;
        noCurr++;
    }
    //get Languges
    let noLang = 0;
    for(const langType in countrey[0].languages){

        if(noLang >= 1){

            lang += ", ";
        }
        lang += countrey[0].languages[langType];
        noLang++;
    }
  }

      // go back to home page
      const navigate = useNavigate();
      function goBack(){
    
        navigate({
          pathname: '/Home',
        });
      }
    
  /************************************************************************/
  useEffect(()=>{

    getCountry();
  },[]);

  return (
    <div className="container flag detailes-size">
      <div className={`back btn bg-transparent ${darkMode ? " spic-dark-mode" : "light"} my-4 all-light-shadow border border-dark`}>
        <button onClick={()=>goBack} href="../home.html"><i className="fa fa-long-arrow-left mx-1" aria-hidden="true" />Back</button>
      </div>
      <div id="flag-details" className="flag-details my-4 gap-5 d-flex flex-column flex-md-row justify-content-between justify-content-md-start flex-column flex-md-row">
        {countrey ? 
        (<>
          <div className="flag-image">
            <img alt="flag-img" src={`${countrey[0].flags.svg}`} />
          </div>
          <div className="flag-contnent w-75">
            <h1 className="flag-name h4 mid-bold">{countrey[0].name.common}</h1>
            <div className="my-4 flag-information d-flex justify-content-between">
                <ul className="px-5">
                  <li className="detailes-size"><span className="home-det mid-bold">Native Name:</span> {nativeName}</li>
                  <li className="detailes-size"><span className="home-det mid-bold">Population:</span> {countrey[0].population}</li>
                  <li className="detailes-size"><span className="home-det mid-bold">Region:</span> {countrey[0].region}</li>
                  <li className="detailes-size"><span className="home-det mid-bold">Sub Region:</span> {countrey[0].subregion}</li>
                  <li className="detailes-size"><span className="home-det mid-bold">Capital:</span> {countrey[0].capital[0]}</li>
                </ul>
                <ul className="px-5">
                  <li className="detailes-size"><span className="home-det mid-bold">Top level domain:</span> {countrey[0].tld[0]}</li>
                  <li className="detailes-size"><span className="home-det mid-bold">Currencies:</span> {currency}</li>
                  <li className="detailes-size"><span className="home-det mid-bold">Languages:</span> {lang}</li>
                </ul>
            </div>
            <div className="borders d-flex gap-4 overflow-auto">
                    <p className="detailes-size home-det mid-bold p-0 m-0 d-flex align-items-center">Border Countries: </p>
                    <ul className="d-flex justify-content-start flex-wrap gap-2 w-75 p-0 m-0">
                        {borderNo ? countrey[0].borders.map((border, index) => (
                            <li key={index} className={`detailes-size border-country ${darkMode ? "spic-dark-mode" : "light"} d-flex align-items-center py-2 px-4 all-Mid-shadow bg-white`}>{border}</li>
                          )) : (
                            <li className={`detailes-size border-country ${darkMode ? "spic-dark-mode" : "light"} d-flex align-items-center py-2 px-4 all-Mid-shadow bg-white`}>No Borders!</li>
                        )}
                    </ul>
            </div>
          </div>
        </>)
        :(<div className='w-100 h-100 my-4 d-flex justify-content-center align-items-center'>
            <div className='d-flex justify-content-center align-items-center'><div className="spinner-border text-primary" role="status">
              <span className="sr-only">Loading...</span>
            </div>
            </div>
        </div>)}
      </div>
   </div>

  );
}

export default FlagDetails;
