import React, { useEffect, useState } from 'react';
import { useDarkMode } from '../customeHooks/DarkModeContext';

function NavBar() {
// handale with mode click btn 
const { darkMode, toggleDarkMode } = useDarkMode();

useEffect(() => {


  }, [darkMode]);

  // Return Elements:
  return (
   <nav id="nav" className={`bottom-shadow navbar navbar-expand-lg bg-body-tertiary position-sticky ${darkMode ? " spic-dark-mode" : "light"} bg-white`}>
        <div className="container">
            <h1 id="logo" className="navbar-brand mid-bold light m-0">Where in the world?</h1>
            <div className="mode d-flex align-items-center">
                <button type="button" onClick={toggleDarkMode} className={`btn ${darkMode ? " spic-dark-mode" : "light"}`} id="mode-btn"><i className={`mx-2 fa-regular ${darkMode ? 'fa-sun' : 'fa-moon'}`}></i>Dark Mode</button>
            </div>
        </div>
    </nav>
  );
}

export default NavBar;
