import React, { useEffect, useState } from "react";
import { useDarkMode } from "../../customeMethods/DarkModeContext";

function FavioriteSection({
  favCountry,
  handleFavCountry,
  favCountries,
  dispFaviorites,
  storeInLocalStorage,
}) {
  const { darkMode } = useDarkMode();
  let favoritBox = document.getElementById("favourit-countries");

  //Hover on Box
  function favDragOver(e) {
    e.preventDefault();
    favoritBox.classList.add("hovered");
  }

  //Leave the Box
  function favDragLeave() {
    favoritBox.classList.remove("hovered");
  }

  //Dropping the Item
  async function favDroped(e) {
    e.preventDefault();
    favoritBox.classList.remove("hovered");
    await storeInLocalStorage(favCountry);
    handleFavCountry({
      area: 0,
      img: "",
      name: "country",
    });
  }

  useEffect(() => {
    dispFaviorites();
  }, [favCountries]);

  return (
    <aside
      className={`fav ${
        darkMode ? " spic-dark-mode" : "light"
      } f-flex flex-column gap-2 d-none d-md-block p-1 d-none d-md-block bg-white`}
    >
      <div className="px-3 py-4">
        <h4 className="h5 mid-bold home-size">Favourites</h4>
      </div>
      <div
        onDragOver={favDragOver}
        onDragLeave={favDragLeave}
        onDrop={favDroped}
        className="px-0 favourit-content d-flex flex-column gap-2"
        id="favourit-countries"
      >
        {favCountries ? dispFaviorites() : ""}
      </div>
    </aside>
  );
}

export default FavioriteSection;
