import React, { useEffect, useState } from 'react'
import axios from 'axios';

//custom hooks
import {useDarkMode} from '../componunts/methods/DarkModeContext.jsx'
import {getAllCountries, getCountryByName} from "../componunts/methods/ApisHandale.jsx"


//components
import SearchBar from '../componunts/SearchBar';
import RegionFilter from '../componunts/RegionFilter';
import Card from '../componunts/card.jsx';




function Home() {
 
  /** Variables **/
  let errorMessage = 
                    <div class="w-100 h-100 d-flex flex-column align-items-center">
                      <div class="alert alert-danger my-4 mid-bold w-100 d-flex justify-content-center">Error!!!</div>
                      <div class="my-4 mid-bold">Theres a proplem! Please wait for us to solve the proplem.</div>
                    </div>;
  let [countries, setCountries] = useState([]);
  let [visipCountries, setVisipCountries] = useState([]);
  let [filterOption, setFilterOption] = useState('noValue');
  let [allCoubntryAPIerror, setallCoubntryAPIerror] = useState('false');
  /** faviorite country and handale it with children **/
  let [favCountry, setFavCountry] = useState({

    area: 0,
    img: "",
    name: "country",
  });
  //get FavoriteBox Element to handle his style
  let favoritBox = document.getElementById("favourit-countries");


  const {darkMode} = useDarkMode();
  
  /** ====================================================================  Fetch Api and save all Countries and handale errors ==================================================================== **/
 
 

// function to get all originalCountries:
async function getCountries() {

  try {
    let data = await getAllCountries();
    setCountries(data);
    setVisipCountries(data);
  } catch (error) {

        setallCoubntryAPIerror(true);
        console.error("Error in fetching originalCountries:", error);
    }
}

/** ==================================================================== Favorite Section ==================================================================== **/
// faviorite countries from local storage
let [favCountries, setFavCountries] = useState([]);
let jsonlocCountries = localStorage.getItem("favCountries");
if (jsonlocCountries) {

  favCountries = JSON.parse(jsonlocCountries);
}


//function to handale the favCountry come from child and save it into favCountry
const handleFavCountry = (country) => {

  setFavCountry(country);
};


//Hover on Box
function favDragOver(e){

  e.preventDefault();
  favoritBox.classList.add('hovered'); 
}

//Leave the Box
function favDragLeave(){

  favoritBox.classList.remove('hovered'); 
}


//Dropping the Item
async function favDroped(e){

  e.preventDefault();
  favoritBox.classList.remove('hovered'); 
  await storeInLocalStorage(favCountry);
  setFavCountry({
    area: 0,
    img: "",
    name: "country",
  });
}
/**  Dealing with Local Storage **/
//Store Faviorits Country in LocalStorage
function storeInLocalStorage(countrey){

    if (!favCountries) {

        favCountries = [];
      }
    let isExist = favCountries.findIndex(favCountries => favCountries.area == countrey.area);
    if(isExist !== -1){

        return;
    }
    favCountries.push(countrey);
    let jsonArrayFav = JSON.stringify(favCountries);
    localStorage.setItem("favCountries", jsonArrayFav);
    setFavCountries(favCountries);
}

// Delete country from local storage
function delCountry(selectedArea) {

  let indexToDelete = favCountries.findIndex(country => country.area == selectedArea);
  
  if (indexToDelete !== -1) {

      favCountries.splice(indexToDelete, 1);
      let jsonArrayFav = JSON.stringify(favCountries);
      localStorage.setItem("favCountries", jsonArrayFav);
      setFavCountries(favCountries);
  }
}
/** ==================================================================== filter Section ==================================================================== **/
//clear results
function clearResults(){

  setVisipCountries(countries);
}
// handle filter option value
function handaleFilterOption(option) {

  setFilterOption(option);

  if (option === 'fav') {
    const filteredFav = countries.filter((country) =>
      favCountries.some((favCountry) => favCountry.area === country.area)
    );
    setVisipCountries(filteredFav);
  } 
  
  else if (option === 'noValue') {
    clearResults();
  } 
  
  else {

    // Check if the option is one of the regions
    const regions = ['Africa', 'Americas', 'Asia', 'Europe', 'Oceania'];
    if (regions.includes(option)) {

        const filteredCountries = countries.filter(
        (country) => country.region === option
      );
      setVisipCountries(filteredCountries);
    }
  }
}

function filterCountries(countries){

  let filteredCountries =  countries.filter((country) => country.region  === filterOption);
  return filteredCountries;
}
/** ==================================================================== Search Section ==================================================================== **/
// handale search bar value
const [inputChanged, setInputChanged] = useState(false); // to handale the changes of user inputs
const [noResults, setNoResults] = useState(false); // new state for tracking no results

// handle search bar value
async function handaleSearchVlue(val) {
  let searchResult;
  if (val === '') {
    clearResults();
  } else {
    setInputChanged(true);
    try {
      let result = await axios.get(`https://restcountries.com/v3.1/name/${val}`);
      if (!inputChanged) {
        if (result.length === 0) {
          setNoResults(true);
        } else {
          searchResult = result;
          setNoResults(false);
          setVisipCountries(filterCountries(searchResult));
        }
      }
    } catch (error) {
      setNoResults(true);
      console.error('Fetch operation failed: ', error);
    }
  }
}

useEffect(() => {
  let mounted = true;
  if (mounted) {
    if (inputChanged) {
      handaleSearchVlue();
    }
  }
  return () => {
    mounted = false;
  };
}, [inputChanged]);

  /***************************************** Use Effect *****************************************/
  useEffect(() => {

  getCountries();
}, []);

useEffect(() => {

}, [favCountries, visipCountries]);

// Return Elements:
  return (
    <>
      <div className='searchSection my-4 d-flex flex-column flex-md-row gap-4 justify-content-between align-items-md-center'>
          <SearchBar handaleSearchVlue={handaleSearchVlue} />
          <RegionFilter handaleFilterOption={handaleFilterOption} />
      </div>
      <div className="content d-flex gap-4">
          <aside className={`fav ${darkMode ? " spic-dark-mode" : "light"} f-flex flex-column gap-2 d-none d-md-block p-1 d-none d-md-block bg-white`}>
            <div className="px-3 py-4">
              <h4 className="h5 mid-bold home-size">Favourites</h4>
            </div>
            <div onDragOver={favDragOver} onDragLeave={favDragLeave} onDrop={favDroped} className="px-0 favourit-content d-flex flex-column gap-2" id="favourit-countries">
              {
                favCountries ? favCountries.map((countrey) => (
                  <div className="d-flex justify-content-md-between">
                    <div className="d-flex gap-2 align-items-center w-75">
                      <div className="fav-image">
                        <img alt="fav-img" src={`${countrey.img}`} />
                      </div>
                      <h5 className="card-title p-0 m-0 mid-bold text-truncate detailes-size">{countrey.name}</h5>
                    </div>
                    <button onClick={()=>delCountry(countrey.area)} className={`bg-transparent py-0 px-2 mid-bold detailes-size ${darkMode ? " spic-dark-mode" : "light"}`}>x</button>
                </div>
              )) : ''
              }
            </div>
          </aside>
          <div className="detailes-contain">
            <div className="row" id="flag-row">
            {Array.isArray(visipCountries) && visipCountries ? visipCountries.map((country, index) => (
              <Card key={index} country={country} handleFavCountry={handleFavCountry} /> // fixed the return statement
            ))
            : noResults ? 
              <div className='my-4 mid-bold'>No results Found.</div>
            : allCoubntryAPIerror ? errorMessage : <div className='d-flex justify-content-center align-items-center'><div className="spinner-border text-primary" role="status">
            <span className="sr-only">Loading...</span>
          </div></div>}
            </div>
          </div>
      </div>

  </>
  );
}

export default Home;
