import React, { useEffect, useState } from 'react';

function NavBar() {
    const savedDarkMode = JSON.parse(localStorage.getItem("darkMode"));
    let [darkMode, setDarkMode] = useState(savedDarkMode || false); 

// handale with mode click btn
  function toggleMode(){ 
        const newDarkMode = !darkMode;
        setDarkMode(newDarkMode);
        localStorage.setItem("darkMode", JSON.stringify(newDarkMode));
    }

    let modeClass = "light";
    darkMode ? modeClass += " spic-dark-mode" : modeClass;
  return (
   <nav id="nav" className="bottom-shadow navbar navbar-expand-lg bg-body-tertiary position-sticky light bg-white">
        <div className="container">
            <h1 id="logo" className="navbar-brand mid-bold light m-0">Where in the world?</h1>
            <div className="mode d-flex align-items-center">
                <button type="button" onClick={()=> toggleMode()} className="btn light" id="mode-btn"><i className="mx-2 fa-regular fa-moon" />Dark Mode</button>
            </div>
        </div>
    </nav>

  );
}

export default NavBar;
