import React, {useState, useEffect} from "react";

function getFavioriteCountries(){

        // faviorite countries from local storage
        let locCountries = [];
        let jsonlocCountries = localStorage.getItem("favCountries");
        if (jsonlocCountries) {

            locCountries = JSON.parse(jsonlocCountries);
        }

        return(locCountries);
    }

export default getFavioriteCountries;
