import React, { useEffect, useState } from "react";
import { useDarkMode } from "../customeMethods/DarkModeContext";
import { useNavigate } from "react-router-dom";

function Card(props) {
  // go to country details page
  const navigate = useNavigate();
  function goToCountry(code) {
    navigate({
      pathname: "/FlagDetails",
      search: `?countryID=${code}`,
    });
  }

  //get Variables from Parent
  const { name, area, altSpellings, flags, population, region, capital, ccn3 } =
    props.country;

  let favCountries = props.favCountries;
  //Dark or not
  const { darkMode } = useDarkMode();

  /** ============================ Favorite Section ============================ **/
  //favCountry variable to handale changes in favivorite countries "Drag & Drop, Clicked btn"
  let [favCountry, setFavCountry] = useState({
    area: 0,
    img: "",
    name: "country",
  });

  //Start of Dragging
  function dragCard(e, area, img, name) {
    e.target.style.opacity = 0.5;
    img = img.toLowerCase();
    let imagURL = `https://flagcdn.com/${img}.svg`;
    favCountry.area = area;
    favCountry.img = imagURL;
    favCountry.name = name;
    props.handleFavCountry(favCountry); // Pass the favCountry data to the parent component
  }
  //End of Dragging
  function endDragCard(e) {
    favCountry = {
      area: 0,
      img: "",
      name: "country",
    };
    e.target.style.opacity = 1;
  }

  // Handale the Star Fav Btn click
  function handaleFavBtn(e, area, img, name) {
    if (e.target.classList.contains("clicked")) {
      e.target.classList.remove("clicked");
      props.deleteCountry(area);
    } else {
      e.target.classList.add("clicked");
      let favClicked = {
        area: 0,
        img: "",
        name: "",
      };
      img = img.toLowerCase();
      let imagURL = `https://flagcdn.com/${img}.svg`;
      favClicked.area = area;
      favClicked.img = imagURL;
      favClicked.name = name;
      props.storeInLocalStorage(favClicked);
    }
  }

  // disply visibillity countries
  function dispFaviorites() {
    return favCountries.map((countrey) => (
      <div className="d-flex justify-content-md-between">
        <div className="d-flex gap-2 align-items-center w-75">
          <div className="fav-image">
            <img alt="fav-img" src={`${countrey.img}`} />
          </div>
          <h5 className="card-title p-0 m-0 mid-bold text-truncate detailes-size">
            {countrey.name}
          </h5>
        </div>
        <button
          onClick={() => deleteCountry(countrey.area)}
          className={`bg-transparent py-0 px-2 mid-bold detailes-size ${
            darkMode ? " spic-dark-mode" : "light"
          }`}
        >
          x
        </button>
      </div>
    ));
  }
  let favBtnContent = ``;
  let isFav = favCountries.findIndex((element) => element.area == area);

  if (isFav !== -1) {
    favBtnContent = (
      <i
        onClick={(e) => handaleFavBtn(e, area, altSpellings[0], name.official)}
        className="fa-solid fa-star clicked"
      ></i>
    );
  } else {
    favBtnContent = (
      <i
        onClick={(e) => handaleFavBtn(e, area, altSpellings[0], name.official)}
        className="fa-solid fa-star"
      ></i>
    );
  }

  /***************************************** Use Effect *****************************************/
  useEffect(() => {
    dispFaviorites();
  }, [props, favCountries]);

  return (
    <>
      <div className="col-lg-4 col-md-6 col-sm-6 col-xsm-12 mb-3">
        <div
          draggable="true"
          onDragStart={(e) => dragCard(e, area, altSpellings[0], name.official)}
          onDragEnd={(e) => endDragCard(e)}
          className="card border-0 position-relative m-1 m-xl-3 mt-0 ${darkModeClass}"
        >
          <div className="image" onClick={() => goToCountry(ccn3)}>
            <img loading="lazy" alt="home-img" src={`${flags.svg}`} />
          </div>
          <div
            onClick={() => goToCountry(ccn3)}
            className={`card-body flag-card form ${
              darkMode ? " spic-dark-mode" : "light"
            }`}
          >
            <h5 className="card-title px-2 my-1 mid-bold text-truncate home-size">
              {name.official}
            </h5>
            <ul className="px-2 mt-3 mb-5">
              <li className="home-size">
                <span className="mid-bold home-size">Population:</span>{" "}
                {population}
              </li>
              <li className="home-size">
                <span className="mid-bold home-size">Region:</span> {region}
              </li>
              <li className="home-size">
                <span className="mid-bold home-size">Capital:</span> {capital}
              </li>
            </ul>
          </div>
          <div
            className="fav-click d-md-none fav-star bg-transparent position-absolute bottom-0 end-0"
            style={{ fontFamily: "FontAwesome" }}
          >
            {favBtnContent}
          </div>
        </div>
      </div>
    </>
  );
}

export default Card;
