import React, { useEffect, useState } from "react";
//custom hooks
import { useDebouncing } from "../hooks/useDebouncing";

//methods
import { useDarkMode } from "../customeMethods/DarkModeContext";
import { getAllCountries, getCountryByName } from "../apis/ApisHandale";
//components
import SearchBar from "../componunts/SearchBar";
import RegionFilter from "../componunts/RegionFilter";
import Card from "../componunts/card.jsx";
import FavioriteSection from "../componunts/homeComponunts/FavioriteSection";

function Home() {
  /** Variables **/
  let errorMessage = (
    <div className="w-100 h-100 d-flex flex-column align-items-center">
      <div className="alert alert-danger my-4 mid-bold w-100 d-flex justify-content-center">
        Error!!!
      </div>
      <div className="my-4 mid-bold">
        Theres a proplem! Please wait for us to solve the proplem.
      </div>
    </div>
  );
  const { darkMode } = useDarkMode();
  let [filterOption, setFilterOption] = useState("noValue");
  //API Variables
  let [countries, setCountries] = useState([]);
  let [visibleCountries, setvisibleCountries] = useState([]);
  let [allCoubntryAPIerror, setallCoubntryAPIerror] = useState(false);
  //faviorite section
  let [favCountry, setFavCountry] = useState({
    area: 0,
    img: "",
    name: "country",
  });
  //search variables
  let [val, setVal] = useState("");
  const [inputChanged, setInputChanged] = useState(false); // to handle the changes of user inputs
  let [noResults, setNoResults] = useState(false);
  const delayInMilliseconds = -1000;
  let timeoutId; // to store the timeout ID
  const debouncedSearchValue = useDebouncing(val, 1000);
  // function to get all originalCountries:
  async function getCountries() {
    try {
      let data = await getAllCountries();
      setCountries(data);
      setvisibleCountries(data);
    } catch (error) {
      setallCoubntryAPIerror(true);
      console.error("Error in fetching originalCountries:", error);
    }
  }

  /** ==================================================================== Favorite Section ==================================================================== **/
  // faviorite countries from local storage
  let [favCountries, setFavCountries] = useState([]);
  let jsonlocCountries = localStorage.getItem("favCountries");
  if (jsonlocCountries) {
    favCountries = JSON.parse(jsonlocCountries);
  }

  /** faviorite country and handale it with children **/
  //function to handale the favCountry come from child and save it into favCountry
  const handleFavCountry = (country) => {
    setFavCountry(country);
  };

  /**  Dealing with Local Storage **/
  const storeInLocalStorage = (country) => {
    if (!favCountries) {
      favCountries = [];
    }
    let isExist = favCountries.findIndex(
      (favCountry) => favCountry.area === country.area
    );
    if (isExist !== -1) {
      return;
    }
    const updatedFavCountries = [...favCountries, country];
    setFavCountries(updatedFavCountries); // Update the state here

    // Save the updated state to localStorage
    let jsonArrayFav = JSON.stringify(updatedFavCountries);
    localStorage.setItem("favCountries", jsonArrayFav);
  };

  function deleteCountry(selectedArea) {
    let indexToDelete = favCountries.findIndex(
      (country) => country.area == selectedArea
    );

    if (indexToDelete !== -1) {
      favCountries.splice(indexToDelete, 1);
      let jsonArrayFav = JSON.stringify(favCountries);
      localStorage.setItem("favCountries", jsonArrayFav);
      setFavCountries(favCountries);
    }
  }
  /** ==================================================================== filter Section ==================================================================== **/
  function clearResults() {
    setvisibleCountries(countries);
  }

  function handaleFilterOption(option) {
    setFilterOption(option);

    if (option === "fav") {
      const filteredFav = countries.filter((country) =>
        favCountries.some((favCountry) => favCountry.area === country.area)
      );
      setvisibleCountries(filteredFav);
    } else if (option === "noValue") {
      clearResults();
    } else {
      // Check if the option is one of the regions
      const regions = ["Africa", "Americas", "Asia", "Europe", "Oceania"];
      if (regions.includes(option)) {
        const filteredCountries = countries.filter(
          (country) => country.region === option
        );
        setvisibleCountries(filteredCountries);
      }
    }
  }

  /** ==================================================================== Search Section ==================================================================== **/
  async function searchCountry(value) {
    setVal(value);
    let searchResult;
    if (val === "") {
      clearResults();
    } else {
      try {
        const result = await getCountryByName(debouncedSearchValue);
        if (result.length === 0) {
          setvisibleCountries([]);
        } else {
          if (filterOption === "noValue") {
            searchResult = result;
          } else {
            searchResult = result;
            // Check if the option is one of the regions
            const regions = ["Africa", "Americas", "Asia", "Europe", "Oceania"];
            if (regions.includes(filterOption)) {
              searchResult = searchResult.filter(
                (country) => country.region === filterOption
              );
            }
          }
          setvisibleCountries(searchResult); // Set the state with the filtered search result
        }
      } catch (error) {
        setallCoubntryAPIerror(true);
        console.error("Fetch operation failed: ", error);
      }
    }
  }

  /***************************************** Display Functions *****************************************/
  function dispCountries() {
    return visibleCountries.map((country, index) => (
      <Card
        key={index}
        country={country}
        handleFavCountry={handleFavCountry}
        favCountries={favCountries}
        dispFaviorites={dispFaviorites}
        storeInLocalStorage={storeInLocalStorage}
        deleteCountry={deleteCountry}
      />
    ));
  }
  function dispFaviorites() {
    return favCountries.map((countrey, index) => (
      <div key={index} className="d-flex justify-content-md-between">
        <div className="d-flex gap-2 align-items-center w-75">
          <div className="fav-image">
            <img alt="fav-img" src={`${countrey.img}`} />
          </div>
          <h5 className="card-title p-0 m-0 mid-bold text-truncate detailes-size">
            {countrey.name}
          </h5>
        </div>
        <button
          onClick={() => deleteCountry(countrey.area)}
          className={`bg-transparent py-0 px-2 mid-bold detailes-size ${
            darkMode ? " spic-dark-mode" : "light"
          }`}
        >
          x
        </button>
      </div>
    ));
  }

  /***************************************** Use Effect *****************************************/
  useEffect(() => {
    getCountries();
  }, []);
  useEffect(() => {
    searchCountry();
  }, [val]);

  // Return Elements:
  return (
    <>
      <div className="searchSection my-4 d-flex flex-column flex-md-row gap-4 justify-content-between align-items-md-center">
        <SearchBar searchCountry={searchCountry} />
        <RegionFilter handaleFilterOption={handaleFilterOption} />
      </div>
      <div className="content d-flex gap-4">
        <FavioriteSection
          favCountry={favCountry}
          handleFavCountry={handleFavCountry}
          favCountries={favCountries}
          dispFaviorites={dispFaviorites}
          storeInLocalStorage={storeInLocalStorage}
          deleteCountry={deleteCountry}
        />
        <div className="detailes-contain">
          <div className="row" id="flag-row">
            {Array.isArray(visibleCountries) ? (
              dispCountries()
            ) : allCoubntryAPIerror ? (
              errorMessage
            ) : !errorMessage ? (
              errorMessage
            ) : visibleCountries.status === 404 || noResults ? (
              <div className="my-4 mid-bold">No results Found.</div>
            ) : (
              <div className="d-flex justify-content-center align-items-center">
                <div className="spinner-border text-primary" role="status">
                  <span className="sr-only">Loading...</span>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

export default Home;
