import React, { useEffect, useState } from 'react'
import { useSearchParams } from 'react-router-dom'
import axios from 'axios';
import {useDarkMode} from '../customeHooks/DarkModeContext'


function FlagDetails() {
  //Dark or not
  const {darkMode} = useDarkMode();

  //get code from url
  let [searchParams, setSearchParams] = useSearchParams();
  let code = searchParams.get('countryID');

  //store country
  let [countrey, setCountrey] = useState();

  //function fetch api country
  async function getCountry(){

    try{

      let {data} = await axios.get(`https://restcountries.com/v3.1/alpha/${code}`);
      setCountrey(data);
    }catch(error) {
      
      console.error('Fetch operation failed: ', error);
    }
   
  }

  
  useEffect(()=>{

    getCountry();
  },[]);

  return (
    <div className="container flag detailes-size">
      <div className={`back btn bg-transparent ${darkMode ? " spic-dark-mode" : "light"} my-4 all-light-shadow border border-dark`}>
        <a href="../home.html"><i className="fa fa-long-arrow-left mx-1" aria-hidden="true" />Back</a>
      </div>
      <div id="flag-details" className="flag-details my-4 gap-5 d-flex flex-column flex-md-row justify-content-between justify-content-md-start flex-column flex-md-row">
        {countrey ?
        <>
          <div className="flag-image">
            <img alt="flag-img" src='' />
          </div>
          <div className="flag-contnent w-75">
            <h1 className="flag-name h4 mid-bold">hh</h1>
          </div>
        </>
        :<div className='d-flex justify-content-center align-items-center'><div class="spinner-border text-primary" role="status">
        <span className="sr-only">Loading...</span>
      </div></div>}
      </div>
   </div>

  );
}

export default FlagDetails;
