import React, { useEffect, useState } from 'react'

function Card(country) {
    //get fav countries
    let favBTN = ``;
    getFaviouriteCountries();

    //handale fav countries
       let favBtnContent = ``;
       let isFav = locCountries.findIndex(element => element.area == country.area);

       if (isFav !== -1) {

           favBtnContent = `<i onclick="handaleFavBtn(event,'${country.area}','${country.altSpellings[0]}', '${country.name.official}')"  class="fa-regular fa-star clicked"></i>`;
       }
       else{

           favBtnContent = `<i onclick="handaleFavBtn(event,'${country.area}','${country.altSpellings[0]}', '${country.name.official}')"  class="fa-regular fa-star"></i>`;
       }     
   

    return (
    <>
        <div className="card border-0 position-relative m-1 m-xl-3 mt-0 light">
            <a draggable="true" ondragstart="dragCard('${country.area}','${country.altSpellings[0]}', '${country.name.official}')" ondragend="endDragCard()" className="position-relative link" href="pages/flagdet.html?code=${country.ccn3}">
                <div className="image">
                <img alt="home-img" src="${country.flags.svg}" />
                </div>
            </a>
        <div className="card-body flag-card form light">
            <h5 className="card-title px-2 my-1 mid-bold text-truncate home-size">${'{'}country.name.official{'}'}</h5>
            <ul className="px-2 mt-3 mb-5">
            <li className="home-size"><span className="mid-bold home-size">Population:</span> ${'{'}country.population{'}'}</li>
            <li className="home-size"><span className="mid-bold home-size">Region:</span> ${'{'}country.region{'}'}</li>
            <li className="home-size"><span className="mid-bold home-size">Capital:</span> ${'{'}country.capital[0]{'}'}</li>
            </ul>
        </div>
        <div className="fav-click d-md-none fav-star bg-transparent position-absolute bottom-0 end-0" style={{fontFamily: 'FontAwesome'}}>${'{'}favBtnContent{'}'}</div>
        </div>

    </>
  );

}
export default SearchBar;
