import React, { useEffect, useState } from 'react'
import DarkMode from '../customeHooks/DarkMode';

function RegionFilter() {
    let { darkMode, toggleDarkMode } = DarkMode();
    let modeClass ="light";

    useEffect(() => {

        if(darkMode){

            modeClass+= " spic-dark-mode";
        }
        else{

            modeClass = "light";
        }
    }, [darkMode]);
  return (

    <select className={`form-select ${darkMode ? " spic-dark-mode" : "light"}`} aria-label="Default select example" name="region" id="region">
        <option value="noValue" selected>Filter by</option>
        <option value="noValue">No Filter</option>
        <option value="fav">Favourites</option>
        <option value="Africa">Africa</option>
        <option value="Americas">America</option>
        <option value="Asia">Asia</option>
        <option value="Europe">Euorpe</option>
        <option value="Oceania">Oceania</option>
    </select>
  );
}

export default RegionFilter;
