    const modeIcone = document.getElementById('mode-icone');

    const toDarkMode = () => {
    document.body.classList.toggle('dark-theme');
    modeIcone.classList.toggle('fa-moon-o');
    modeIcone.classList.toggle('fa-sun-o');
    for (const element of elements) {
        element.classList.toggle('spic-dark-mode');
    }
    };
    useEffect(() => {

    }, [darkMode]);

    return { darkMode, toggleDarkMode };
};

export default toDark;
